import { Screen } from "../components/Screen";
import ReactXnft, { Button,  Text, View, usePublicKey } from "react-xnft";
import { Keypair, SystemProgram, Transaction } from "@solana/web3.js";




ReactXnft.events.on("connect", () => {

});

export function HomeScreen() {
  const publicKey = usePublicKey()

  // const onClick = () => {
  // window.xnft.solana.signMessage(new TextEncoder().encode('If you Agree with this message please approve it'))
  // }
  const onClick = () => {
    window.xnft.solana.signTransaction(
      new Transaction().add(
        SystemProgram.transfer({
          fromPubkey: publicKey,
          toPubkey: Keypair.generate().publicKey,
          lamports: 1,
        })
      )
    )
  }


  return (
    <View>
      <Text >Hi Blockchain Guyes </Text>
      <Button onClick={onClick}>Sign your Message</Button>
    </View>
  );
  }
